import { Menu, Transition } from '@headlessui/react';
import {
  ChevronDownIcon,
  ChevronLeftIcon,
  ChevronRightIcon,
} from '@heroicons/react/24/solid';
import * as React from 'react';
import { Fragment, useState } from 'react';
import './style.css';

export default function App() {
  const [submenuVisible, setSubmenuVisible] = useState(false);
  return (
    <div className="flex w-full h-screen flex-col items-center justify-center bg-gradient-to-r to-indigo-500 from-purple-500">
      <Menu as="div" className="relative inline-block text-left">
        {({ close, open }) => (
          <Fragment>
            <div>
              <Menu.Button className="inline-flex w-full justify-center rounded-md bg-violet-600 px-4 py-2 text-sm font-medium text-white hover:bg-violet-700 focus:outline-none focus-visible:ring-2 focus-visible:ring-white focus-visible:ring-opacity-75">
                Options
                <ChevronDownIcon
                  className="ml-2 -mr-1 h-5 w-5 text-violet-200 hover:text-violet-100"
                  aria-hidden="true"
                />
              </Menu.Button>
            </div>
            <Menu.Items className="absolute right-0 mt-2 w-56 origin-top-right divide-y divide-gray-100 rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none">
              <div className="px-1 py-1 ">
                {!submenuVisible ? (
                  <Menu.Item>
                    {({ active }) => (
                      <button
                        className={`${
                          active ? 'bg-violet-500 text-white' : 'text-gray-900'
                        } group flex w-full items-center rounded-md px-2 py-2 text-sm flex items-center`}
                        onClick={(e) => {
                          e.preventDefault();
                          setSubmenuVisible((prev) => !prev);
                        }}
                      >
                        Nested Menu
                        <ChevronRightIcon className="h-5 w-5 mr-2" />
                      </button>
                    )}
                  </Menu.Item>
                ) : (
                  <button
                    className="bg-violet-500 text-white group flex w-full items-center rounded-md px-2 py-2 text-sm flex items-center justify-between mb-2 border border-b-gray-100"
                    onClick={(e) => {
                      e.preventDefault();
                      setSubmenuVisible((prev) => !prev);
                    }}
                  >
                    <ChevronLeftIcon className="h-5 w-5 mr-2" />
                    Nested menu
                  </button>
                )}
                {submenuVisible && (
                  <Fragment>
                    <Menu.Item>
                      {({ active }) => (
                        <button
                          className={`${
                            active
                              ? 'bg-violet-500 text-white'
                              : 'text-gray-900'
                          } group flex w-full items-center rounded-md px-2 py-2 text-sm`}
                        >
                          Item 1
                        </button>
                      )}
                    </Menu.Item>
                    <Menu.Item>
                      {({ active }) => (
                        <button
                          className={`${
                            active
                              ? 'bg-violet-500 text-white'
                              : 'text-gray-900'
                          } group flex w-full items-center rounded-md px-2 py-2 text-sm`}
                        >
                          Item 2
                        </button>
                      )}
                    </Menu.Item>
                  </Fragment>
                )}
              </div>
              {!submenuVisible && (
                <>
                  <div className="px-1 py-1">
                    <Menu.Item>
                      {({ active }) => (
                        <button
                          className={`${
                            active
                              ? 'bg-violet-500 text-white'
                              : 'text-gray-900'
                          } group flex w-full items-center rounded-md px-2 py-2 text-sm`}
                        >
                          Normal Item 1
                        </button>
                      )}
                    </Menu.Item>
                    <Menu.Item>
                      {({ active }) => (
                        <button
                          className={`${
                            active
                              ? 'bg-violet-500 text-white'
                              : 'text-gray-900'
                          } group flex w-full items-center rounded-md px-2 py-2 text-sm`}
                        >
                          Normal Item 2
                        </button>
                      )}
                    </Menu.Item>
                  </div>
                </>
              )}
            </Menu.Items>
          </Fragment>
        )}
      </Menu>
    </div>
  );
}
